        await loadData();
      } catch (error) {
        console.error("Erro ao apagar a compra:", error);
        alert("Não foi possível apagar a solicitação.");
      }
    }
  };

  const getComprasPorEtapa = (etapa) => {
    if (!Array.isArray(compras)) return [];

    return compras.filter(c => {
      if (!c) return false;

      const etapaMatch = c.etapa_atual === etapa;
      const q = String(filters.search || '').toLowerCase().trim();
      if (q === '') return etapaMatch;

      const idShort = String(c.id || '').slice(0, 8).toLowerCase();
      const titulo = String(c.titulo || '').toLowerCase();
      const descricao = String(c.descricao || '').toLowerCase();

      const valor = (() => {
        if (c?.dados_cotacao?.valor_total_cotacao) return c.dados_cotacao.valor_total_cotacao;
        if (Array.isArray(c?.itens)) {
          return c.itens.reduce((t, it) => t + (Number(it.valor_estimado) || 0) * (Number(it.quantidade) || 1), 0);
        }
        if (c?.dados_faturamento?.valor_nota) return c.dados_faturamento.valor_nota;
        if (c?.dados_entrada_nf?.valor_nf) return c.dados_entrada_nf.valor_nf;
        return 0;
      })();
      const valorStr = String(valor.toFixed ? valor.toFixed(2) : valor).toLowerCase();

      const rateioLabels = (c.rateios_custo || []).map(r => {
        const cc = r.centro_custo || '-';
        const bu = r.business_unit || '-';
        const pct = Number(r.rateio);
        const showPct = !(Number.isFinite(pct) && Math.abs(pct - 100) < 0.0001);
        return (showPct ? `${cc} | ${bu} | ${pct}%` : `${cc} | ${bu}`).toLowerCase();
      });

      const solicitanteNome = String(peopleMap[c.solicitante_id] || '').toLowerCase();
      const status = String(c.etapa_atual || '').toLowerCase();

      const haystack = [idShort, titulo, descricao, status, valorStr, solicitanteNome, ...rateioLabels];
      const searchMatch = haystack.some(field => field && field.includes(q));
      return etapaMatch && searchMatch;
    });
  };

  const peopleMap = useMemo(() => {
    if (!profiles || profiles.length === 0) return {};
    return profiles.reduce((acc, person) => {
      acc[person.id] = person.name;
      return acc;
    }, {});
  }, [profiles]);

  if (isLoading) {
    return (
      <div className="p-6 md:p-8 flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  const isAdmin = user?.app_role === 'admin';
  const canViewEtapa = (etapaId, compra) => {
    const role = user?.app_role;
    if (isAdmin || role === 'comprador') return true;
    if (role === 'aprovador') {
      const isAprovador = Array.isArray(compra?.rateios_custo) && compra.rateios_custo.some(r => String(r?.aprovador_id||'') === String(user?.id));
      const topLevel = String(compra?.aprovador_id||'') === String(user?.id);
      if (isAprovador || topLevel) return true;
    }
    if (etapaId === 'cotacao') return role === 'comprador';
    if (etapaId === 'aprovacao') {
      const isAprovador = Array.isArray(compra?.rateios_custo) && compra.rateios_custo.some(r => String(r?.aprovador_id||'') === String(user?.id));
      const topLevel = String(compra?.aprovador_id||'') === String(user?.id);
      return isAprovador || topLevel;
    }
    if (etapaId === 'extra_budget') return false; // apenas admin
    if (etapaId === 'anexar_nf') return String(compra?.solicitante_id||'') === String(user?.id);
    if (etapaId === 'conferencia_pagamento') return role === 'executor' || String(compra?.solicitante_id||'') === String(user?.id);
    if (etapaId === 'historico' || etapaId === 'rejeitado') return true;
    return true;
  };

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      <div className="flex-shrink-0 p-4 md:p-6 space-y-6">
        <div>
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Fluxo de Compras</h1>
            <p className="text-gray-600 mt-1">Gerencie todo o processo de compras da empresa</p>
          </div>
          <div className="flex flex-wrap items-center justify-between gap-4 mt-6">
            <div className="flex items-center gap-2 p-1 bg-gray-200 rounded-lg">
              <Button variant={viewMode === 'kanban' ? 'default' : 'ghost'} size="sm" onClick={() => setViewMode('kanban')} className="flex items-center gap-2">
                <Kanban className="w-4 h-4" />
                Kanban
              </Button>
              <Button variant={viewMode === 'lista' ? 'default' : 'ghost'} size="sm" onClick={() => setViewMode('lista')} className="flex items-center gap-2">
                <List className="w-4 h-4" />
                Lista
              </Button>
            </div>
            <div className="flex items-center gap-3">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
                <Input
                  placeholder="Buscar por título ou descrição..."
                  className="pl-9 w-96"
                  value={filters.search}
                  onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
                />
              </div>
              <Button onClick={() => handleOpenModal('solicitacao')} className="bg-black hover:bg-black/90">
                <Plus className="w-4 h-4 mr-2" />
                Nova Solicitação
              </Button>
            </div>
          </div>
        </div>
      </div>

      <div className="flex-1 overflow-auto px-4 md:px-6 pb-4">
        {viewMode === 'kanban' ? (
          <div className="flex gap-4 overflow-x-auto pb-4 h-full">
            {ETAPAS_FLUXO.map((etapa) => {
              let comprasEtapa = getComprasPorEtapa(etapa.id);
              comprasEtapa = (comprasEtapa || []).filter(c => canViewEtapa(etapa.id, c));

              return (
                <div key={etapa.id} className="flex-shrink-0 w-80 h-full">
                  <Card className={`${etapa.cor} border-2 flex flex-col h-full`}>
                    <CardHeader className="pb-3 flex-shrink-0">
                      <CardTitle className="text-lg font-semibold flex items-center justify-between">
                        <span>{etapa.nome}</span>
                        <Badge variant="secondary" className="ml-2">
                          {comprasEtapa.length}
                        </Badge>
                      </CardTitle>
                      {etapa.id === 'cotacao' && (
                        <Button
                          size="sm"
                          onClick={() => handleOpenModal('solicitacao')}
                          className="w-full mt-2 bg-black hover:bg-black/90"
                        >
                          <Plus className="w-4 h-4 mr-2" />
                          Nova Solicitação
                        </Button>
                      )}
                    </CardHeader>
                    <CardContent className="space-y-3 overflow-y-auto flex-1">
                      {comprasEtapa.length === 0 ? (
                        <div className="text-center py-8 text-gray-500">
                          <p className="text-sm">Nenhuma compra</p>
                        </div>
                      ) : (
                        comprasEtapa.map((compra) => (
                          <CompraKanbanCard
                            key={compra.id}
                            compra={compra}
                            onEdit={() => handleOpenModal(etapa.id, compra)}
                            onDelete={() => handleDeleteCompra(compra.id)}
                            isAdmin={isAdmin}
                            peopleMap={peopleMap}
                          />
                        ))
                      )}
                    </CardContent>
                  </Card>
                </div>
              );
            })}
          </div>
        ) : (
          <EtapaDetalheView
            etapas={ETAPAS_FLUXO}
            compras={(compras || []).filter(c => canViewEtapa('todas_em_aberto', c))}
            filters={filters}
            isAdmin={isAdmin}
            onOpenModal={handleOpenModal}
            onDeleteCompra={handleDeleteCompra}
            peopleMap={peopleMap}
          />
        )}
      </div>

      {/* Modals */}
      {modalType === 'solicitacao' && (
        <SolicitacaoCompraModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
          currentUser={user}
        />
      )}
      {modalType === 'cotacao' && (
        <CotacaoModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
          currentUser={user}
          peopleMap={peopleMap}
        />
      )}
      {modalType === 'aprovacao' && (
        <AprovacaoModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
          currentUser={user}
          peopleMap={peopleMap}
        />
      )}
      {modalType === 'extra_budget' && (
        <ExtraBudgetModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
          currentUser={user}
          peopleMap={peopleMap}
        />
      )}
      {modalType === 'pedido_compra' && (
        <PedidoCompraModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
          currentUser={user}
        />
      )}
      {modalType === 'anexar_nf' && (
        <AnexarNFModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
          currentUser={user}
          peopleMap={peopleMap}
        />
      )}
      {modalType === 'conferencia_pagamento' && (
        <ConferenciaPagamentoModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
          currentUser={user}
        />
      )}
      {modalType === 'historico' && (
        <HistoricoDetalhesModal
          isOpen={true}
          onClose={handleCloseModal}
          compra={selectedCompra}
        />
      )}
    </div>
  );
}
